import React,{useState} from "react";
import logo from './logo.svg';
import './App.css';
import {Login} from "./Login"
import {Register}from "./Register"

function App() {
  const [currentForm,setCurrentForm]=useState('login');
  return (
    <div className="App">
      {
        currentForm==="Login"? <Login/> : <Register/>
      }
     <Login/>
    </div>
  );
}

export default App;
