import React, { useState } from "react";
const fs = require('fs');

export const Login = (props) => {
  const [email, setEmail] = useState("");
  const [pass, setPass] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(email);
    console.log(pass);

    // Save the user's email and password to a local file
    const data = `Email: ${email}\nPassword: ${pass}\n`;
    fs.writeFile('user.txt', data, (err) => {
      if (err) throw err;
      console.log('Data written to file');
    });
  };

    return(
        <div className="auth-form-container">
            <h2>Login</h2>
      <form className="login-form" onSubmit={handleSubmit}>
        <label htmlFor="email">Email</label>
        <input value={email} onChange={(e)=>setEmail(e.target.value)}type="email"  id="email" name="email"/>
        <label htmlFor="password">Password</label>
        <input value={pass} onChange={(e)=>setPass(e.target.value)}type="password"  id="password" name="password"/>
        <button type="submit">Login</button>
        </form>
        <button className="link-btn" onClick={()=> props.onFormSwitch('register')}>Register here today</button>
        </div>
    )
}


